import React from 'react';
import './sendpage.css';
import MailTemplate from './mailTemplates';
import {connect} from 'react-redux';


function SendPage(state) {
    console.log(state.templates.length)
    const temps = () =>  {
        if(state.templates.length = 0){
            return <div classname="templateHolder"> No templates</div>
        }else 
          state.templates.map(templa=> <MailTemplate key={templa.key} name={templa.name}></MailTemplate>)
      }
    return (
      <div className="sendpage">
          <div className="templateWrapper">
            {temps()}
          </div>  
      </div>
    );
  }

const mapStateToProps = (state) =>{
  return state
}

export default connect(mapStateToProps)(SendPage);
