import { combineReducers } from 'redux';

const initialUser = {
    username: "",//string
    password: "", //string
    email: "",    //string
    id: 0, //number
    token: ""//string
}

const storeUser = (user = initialUser, action) => {
    if (action.type === "STORE_USER") {
        return action.payload
    }
    return user
};


export default combineReducers({
    userData: storeUser
});





















