import React, { useRef } from 'react';
import { render } from 'react-dom'
import './App.css';
import TopNav from './components/topNav';
import LowNav from './components/lowNav';
import FirstBody from './components/firstBody';
import MailEditor from './components/mailEditor';



function App() {
  let activeDic = {sendActive:false, mailActive:false}

  const activecheker =  (sendAct, mailAct) => {
    if(sendAct){
      activeDic.sendActive = true
    }
    if(mailAct){
      activeDic.mailActive = true
    }
  }
  return (
    <div className="primaryWindow">
      {/* <div className="navs"> */}
          <TopNav activeDic={activeDic}></TopNav>
          <LowNav></LowNav>
      {/* </div> */}
      <div className="midBodies">
          <div className="mailEditor">
            <MailEditor></MailEditor>
          </div> 
          <FirstBody activecheker={activecheker} className="midNavs"></FirstBody> 
      </div>
    </div>
  );
}

export default App;
