import React from 'react';
import './buttons.css'
import {useSpring, config,animated } from 'react-spring'
import Styles from './animation' 


export const ButtonPop = ({radius,min=0,grow}) => {
    const delay= 35
    const [lengthSVG, setLengthSVG] =useSpring(() => ({width:radius+"vw", height:radius+"vw"}))
    const [length, setLength] =useSpring(() => ({width:radius*7.5+"%", height:radius*7.5+"%"}))
    const [divLength, setDivLength] =useSpring(() => ({width:radius+"vw",height:radius+"vw","min-width":min,"min-height":min}))
    const [svgLength, setSvgLength] =useSpring(() => ({transform:"translate(0%)"}))
    return ( 
        <animated.div className="button" 
            style={divLength}
            onMouseEnter={() => {
                setDivLength({width:grow+"vw",height:radius+"vw","min-width":min,"min-height":min,delay:delay})
                setLength({width:radius*7.5+"%", height:radius*7.5+"%",delay:delay})
                setSvgLength({transform:"translate("+ grow*-1.58 + "%)"})
                // setLengthSVG({width:grow*1.5+"vw", height:radius+"vw",delay:delay})
            }}
            onMouseLeave={() => {
                setDivLength({width:radius,height:radius,"min-width":min,"min-height":min})
                setLength({width:"75%", height:"75%"})
                setSvgLength({transform:"translate(0%)",delay:delay})
                // setLengthSVG({width:radius, height:radius})
            }}
            >
            <animated.div className="buttonSVGWrapper" style={svgLength} >
                <animated.svg className="buttonSVG" 
                xmlns="http://www.w3.org/2000/svg"
                width = {lengthSVG.width}
                height = {lengthSVG.height}
                version="1.1"
                viewBox="0 0 16.835 15.632"
                >
                <defs>
                    <filter
                    id="filter973"
                    width="1.278"
                    height="1.306"
                    x="-0.139"
                    y="-0.153"
                    colorInterpolationFilters="sRGB"
                    >
                    <feGaussianBlur stdDeviation="0.763"></feGaussianBlur>
                    </filter>
                </defs>
                <g
                    fillOpacity="1"
                    strokeWidth="0.008"
                    transform="translate( -54.046  -143.913)"
                >
                    <animated.rect
                    width = {length.width}
                    height={length.height}
                    x="56.2"
                    y="145.744"
                    fill="#d9d9d9ff"
                    filter="url(#filter973)"
                    opacity="0.799"
                    rx="5.476"
                    ry="5.985"
                    style={{ mixBlendMode: "normal" }}
                    ></animated.rect>
                    <animated.rect
                    width = {length.width}
                    height={length.height}
                    x="56.383"
                    y="146.041"
                    fill="#fff"
                    rx="6.065"
                    ry="5.666"
                    ></animated.rect>
                </g>
            </animated.svg>
            </animated.div>
        </animated.div> 
    );
}
 
export const ButtonPopRight = ({grow, contract}) => {
    
    const [tranform,setTranform] = useSpring(()=>({width:"12.131", x:"56.383"}))
    const [tranformBack,setTranformBack] = useSpring(()=>({width:"13.173", x:"55.877"}))
    const [tranformSVG,setTranformSVG] = useSpring(()=>({width:`${grow.radius}vw`, height:`${grow.radius}vw`}))
    
    const shrink = () => {
        setTranform({width:"12.131", x:"56.383"})
        setTranformBack({width:"13.173", x:"55.877"})
        setTranformSVG({width:`${grow.radius}vw`, height:`${grow.radius}vw`})
    }

    const expand = () => {
        setTranform({width:`${grow.front}`, x:`${grow.transFront}`})
        setTranformBack({width:`${grow.halo}`, x:`${grow.transBack}`})
        setTranformSVG({width:`${grow.SVG}vw`, height:`${grow.radius}vw`})
    }

    contract ? shrink() : expand()

    return ( 
            <animated.svg
                className="buttonSVG"
                xmlns="http://www.w3.org/2000/svg"
                width={tranformSVG.width}
                height={tranformSVG.height}
                version="1.1"
                viewBox="0 0 16.835 15.632"
                >
                <defs>
                    <filter
                    id="filter973"
                    width="1.278"
                    height="1.306"
                    x="-0.139"
                    y="-0.153"
                    colorInterpolationFilters="sRGB"
                    >
                    <feGaussianBlur stdDeviation="0.763"></feGaussianBlur>
                    </filter>
                </defs>
                <g
                    fillOpacity="1"
                    strokeWidth="0.008"
                    transform="translate(-54.046 -143.913)"
                >
                    <animated.rect
                    width={tranformBack.width}
                    height="11.97"
                    x={tranformBack.x}
                    y="145.744"
                    fill="#c6c6c6"
                    filter="url(#filter973)"
                    opacity="0.799"
                    rx="5.476"
                    ry="5.985"
                    style={{ mixBlendMode: "normal" }}
                    ></animated.rect>
                    <animated.rect
                    width={tranform.width}
                    height="11.331"
                    x={tranform.x}
                    y="146.041"
                    fill="#fff"
                    rx="6.065"
                    ry="5.666"
                    ></animated.rect>
                </g>
                </animated.svg>
    );
}
