import { combineReducers } from 'redux';
import { ActionTypes } from '../actions'

let lastCamp = 0

const initialUser = {
    username: "",//string
    password: "", //string
    email: "",    //string
    id: 0, //number
    token: ""//string
}


const storeUser = (user = initialUser, action) => {
    if (action.type === ActionTypes.STORE_USER) {
        return action.payload
    }
    return user
};

const storeCampaign = (capmpaigns = [], action) => {
    if (action.type === ActionTypes.STORE_CAMPAIGN) {
        return [...capmpaigns, { ...action.payload, id: ++lastCamp }]
    }
    return capmpaigns
};


const rootReducer = combineReducers({
    userData: storeUser,
    capmpaigns: storeCampaign
});

export default rootReducer



















