                    style={spinEntrySave}
                    className="saveButton buttons">
                    <svg
                        xmlns="http://www.w3.org/2000/svg"
                        width="263.061"
                        height="157.143"
                        version="1.1"
                        viewBox="0 0 69.601 41.577"
                        >
                        <defs>
                            <path id="rect839" d="M33.262 78.619H67.28V99.03H33.262z"></path>
                        </defs>
                        <g transform="translate(-29.34 -71.815)">
                            <rect
                            width="65.638"
                            height="37.753"
                            x="33.303"
                            y="75.64"
                            fill="#000"
                            fillOpacity="0.319"
                            strokeWidth="0.06"
                            ry="8.186"
                            ></rect>
                            <rect
                            width="65.638"
                            height="37.753"
                            x="29.34"
                            y="71.815"
                            fill="#fff"
                            fillOpacity="1"
                            strokeWidth="0.06"
                            ry="8.186"
                            ></rect>
                            <text
                            transform="translate(22.684 5.234)"
                            style={{
                                lineHeight: "1.25",
                                InkscapeFontSpecification: "'Times New Roman, Bold'",
                                fontVariantLigatures: "normal",
                                fontVariantCaps: "normal",
                                fontVariantNumeric: "normal",
                                fontVariantEastAsian: "normal",
                                whiteSpace: "pre",
                            }}
                            fill="#c10300"
                            fillOpacity="1"
                            stroke="none"
                            fontFamily="Times New Roman"
                            fontSize="14.111"
                            fontStretch="normal"
                            fontStyle="normal"
                            fontVariant="normal"
                            fontWeight="bold"
                            xmlSpace="preserve"
