import React from 'react';
import {} from 'react-dom';
import './buttons.css'
import {useSpring, config,animated } from 'react-spring'

export const ButtonPopRight = ({grow, contract,children}) => {
    const [textWidth,setTextWidth] = useSpring(()=>({width:"0vw",opacity:0,fontSize:`${grow.fontSize}vw`}))
    const [tranform,setTranform] = useSpring(()=>({width:"12.131", x:"56.383"}))
    const [tranformBack,setTranformBack] = useSpring(()=>({width:"13.173", x:"55.877"}))
    const [tranformWrapper,setTranformWrapper] = useSpring(()=>({width:`${grow.radius}vw`, height:`${grow.radius}vw`, minWidth:grow.min,  minHeight:grow.min}))
    const [tranformSVG,setTranformSVG] = useSpring(()=>({width:"100%", height:"100%"}))
    
    const shrink = () => {
        setTranform({width:"12.131", x:"56.383"})
        setTranformBack({width:"13.173", x:"55.877"})
        setTranformSVG({width:"100%", height:"100%"})
        setTranformWrapper({width:`${grow.radius}vw`, height:`${grow.radius}vw`,minWidth:grow.min,  minHeight:grow.min})
        setTextWidth({width:"0vw",opacity:0,fontSize:`${grow.fontSize}vw`})
    }

    const expand = () => {
        setTranform({width:`${grow.front}`, x:`${grow.transRect}`})
        setTranformBack({width:`${grow.front}`, x:`${grow.transRect}`})
        setTranformSVG({width:`${grow.SVG}%  `, height:"100%"})
        setTranformWrapper({width:`${grow.SVG}vw`, height:`${grow.radius}vw`,minWidth:grow.min,  minHeight:grow.min})
        setTextWidth({width:`${grow.textWidth}vw`,opacity:1,fontSize:`${grow.fontSize}vw`})
    }

    contract ? shrink() : expand()
    try{
        return (  
            <animated.div className="buttonWrapper" style={tranformWrapper}>           
                <animated.svg
                    className="buttonSVG"
                    xmlns="http://www.w3.org/2000/svg"
                    width={tranformSVG.width}
                    height={tranformSVG.height}
                    minHeight={tranformSVG.minHeight}
                    minWidth={tranformSVG.minWidth}
                    version="1.1" 
                    viewBox="0 0 16.835 15.632"
                    >
                    <defs>
                        <filter
                        id="filter973"
                        width="1.278"
                        height="1.306"
                        x="-0.139"
                        y="-0.153"
                        colorInterpolationFilters="sRGB"
                        >
                        <feGaussianBlur stdDeviation="0.763"></feGaussianBlur>
                        </filter>
                    </defs>
                    <g
                        fillOpacity="1"
                        strokeWidth="0.008"
                        transform="translate(-54.046 -143.913)"
                    >   
                        <animated.rect
                            style={{ mixBlendMode: "normal" }}
                            width={tranformBack.width}
                            height="11.97"
                            x={tranformBack.x}
                            y="145.744"
                            fill="#000"
                            fillOpacity="0.214"
                            filter="url(#filter973)"
                            opacity="0.799"
                            rx="5.476"
                            ry="5.985"
                        ></animated.rect>
                        <animated.rect
                        width={tranform.width}
                        height="11.331"
                        x={tranform.x}
                        y="146.041"
                        fill="#fff"
                        rx="6.065"
                        ry="5.666"
                        ></animated.rect>
                    </g>
                    </animated.svg>
                    <div style={{width:`${grow.radius}vw`, height:`${grow.radius}vw`, minWidth:grow.min,  minHeight:grow.min}} className="SVGcontainer">
                    <animated.div style={textWidth} className="Text">{grow.text}</animated.div>
                    {children}
                </div>
            </animated.div>
 
        );
    }catch{
        return(
            console.log("A Button should look like this and also have the same attributes <ButtonPopRight ontract={contract} grow={{radius:4, SVG:6, front:20, transRect:52,textWidth:4,fontSize:1, text:\"lets See\"}}><div className=\"child\"></div></ButtonPopRight>")
            )
    }
};
