import React from 'react';
import './sendpage.css';
import MailTemplate from './mailTemplates';
import {connect} from 'react-redux';


function SendPage(state) {
    const temps =  state.templates.map(templa=> <MailTemplate key={templa.key} name={templa.name}></MailTemplate>)
  

    return (
      <div className="sendpage">
          <div className="templateWrapper">
            {temps}
          </div>  
      </div>
    );
  }

const mapStateToProps = (state) =>{
  return state
}

export default connect(mapStateToProps)(SendPage);
