import React from 'react';
import './sendpage.css';
import MailTemplate from './mailTemplates';
function SendPage({templates, manageTemplates}) {
    let x = templates.map(templa=> <MailTemplate key={templa.key} name={templa.name}></MailTemplate>)
    return (
      <div className="sendpage">
          <div className="templateWrapper">
            {x}
          </div>  
      </div>
    );
  }

export default SendPage;
