import React, { useRef, useEffect, useState,useCallback } from 'react';
import { render, unmountComponentAtNode } from 'react-dom'
import './App.css';
import TopNav from './components/topNav';
import LowNav from './components/lowNav';
import FirstBody from './components/firstBody';
import MailEditor from './components/mailEditor';



function App() {
  const [activeDic, setActiveDic] = useState({activeSend:false, activeMail:false})

  // if (activeDic.activeMail){
  //   setTimeout(render(<MailEditor></MailEditor>, document.getElementsByClassName("MailEditor")[0]),10000)
  // }

  return (
      <div className="primaryWindow">
        {/* <div className="navs"> */}
            <TopNav activeDic={activeDic}></TopNav>
            <LowNav></LowNav>
        {/* </div> */}
        <div className="midBodies">
            <div className="MailEditor">
            </div> 
            <FirstBody handelDic={setActiveDic}  className="midNavs"></FirstBody> 
        </div>
      </div>
  );
}

export default App;
