import React, { useRef, useEffect, useState,useCallback } from 'react';
import { render } from 'react-dom'
import './App.css';
import TopNav from './components/topNav';
import LowNav from './components/lowNav';
import FirstBody from './components/firstBody';
import MailEditor from './components/mailEditor';



function App() {
  const [activeDic, setActiveDic] = useState({activeSend:false, activeMail:false})
  const handelDic = useCallback((active) => {
    if(active === "send"){
      setActiveDic({activeSend:true, activeMail:false})
    }
    if(active === "mail"){
      setActiveDic({activeSend:false, activeMail:true})
    }
  },[activeDic])

  return (
    <div className="primaryWindow">
      {/* <div className="navs"> */}
          <TopNav></TopNav>
          <LowNav></LowNav>
      {/* </div> */}
      <div className="midBodies">
          <div className="mailEditor">
            <MailEditor></MailEditor>
          </div> 
          <FirstBody handelDic={handelDic}  className="midNavs"></FirstBody> 
      </div>
    </div>
  );
}

export default App;
