import React from 'react';
import './sendpage.css';
import MailTemplate from './mailTemplates';
import {connect} from 'react-redux';


function SendPage(state,{loadTheTemplate}) {
    const temps = () =>  {
        if(state.templates.length === 0){
            return <div className="templateHolder">Empty. Please Create templates</div>
        }else 
          return state.templates.map(templa=> <MailTemplate loadTheTemplate={loadTheTemplate} key={templa.key} name={templa.name} data={templa.data}></MailTemplate>)
      }
    return (
      <div className="sendpage">
          <div className="templateWrapper">
            {temps()}
          </div>  
      </div>
    );
  }

const mapStateToProps = (state) =>{
  return state
}

export default connect(mapStateToProps)(SendPage);
