import React, { useRef, useEffect, useState,useCallback } from 'react';
import { render, unmountComponentAtNode } from 'react-dom'
import {HashRouter, Route, Switch} from 'react-router-dom'

import {loadTempalteAct} from '../actions'
import './App.css';
import TopNav from './topNav';
import LowNav from './lowNav';
import FirstBody from './firstBody';
import MailEditor from './mailEditor';
import LowButtons from './lowButtons';
import SendPage from './sendPage';
import {connect } from 'react-redux'
import EmailEditor from 'react-email-editor';




function App({loadTemplate}) {
  const [activeDic, setActiveDic] = useState({activeSend:false, activeMail:false})

  const emailEditorRef = useRef(null);
  useEffect(()=>{
    const exportHtml = () => {
      emailEditorRef.current.editor.exportHtml(({ design, html }) => {
        console.log(design)
      });
    };
    render(<LowButtons emailEditorRef={emailEditorRef} exportHtml={exportHtml} activeDic={activeDic} handelDic={setActiveDic}></LowButtons>, document.getElementsByClassName("mailEditorButtons")[0])
  })


  const loadTheTemplate = () => {
    if (!loadTemplate.length ===0){
      emailEditorRef.current.editor.loadDesign(loadTemplate[0])
    }
  }

  return (
      <HashRouter>
        <div className="primaryWindow">
              <div className="top">
                <TopNav activeDic={activeDic}></TopNav>
              </div>
              <div className="low">
                <LowNav></LowNav>
              </div>
          <div className="midBodies">
              <div className="MailEditor">
              </div> 
              <div className="midButtons"> 
                <button onClick={() => exportHtml()}></button>
                <Switch>
                  <Route path="/mailEditor" render={()=><div className="editorWindow"><EmailEditor ref={emailEditorRef}></EmailEditor></div>}></Route>
                  <Route path="/sendPage" render={()=><SendPage loadTheTemplate={loadTempalteAct}></SendPage>}></Route>
                  <Route exact path="/" render={()=><FirstBody  handelDic={setActiveDic} ></FirstBody>}></Route>
                </Switch>
              </div>
          </div>
          <div className="mailEditorButtons">

          </div>
        </div>
      </HashRouter>
  );
} 

const mapStateToProps = (state) =>{
  return state
}
export default connect(mapStateToProps,{loadTempalteAct})(App);
