import React, { forwardRef } from 'react';
import { } from 'react-dom';
import './buttons.css'
import { useSpring, config, animated } from 'react-spring'

export const ButtonPopRightLite = () => {
    const [button, setButton] = useSpring(() => ({ width: "5%", height: "10%" }))
    const [round, setRound] = useSpring(() => ({ round1: "50%", round2: "50%", widthfont: "90%", xfront: "5%" }))
    const [rest, setRest] = useSpring(() => ({ widthfont: "90%", xfront: "5%" }))

    return (
        <React.fragment>
            <animated.div className="svgHolder" style={{ width: button.height, height: button.height }}></animated.div>
            <animated.div className="ten" style={button}>

                <svg
                    width="100%"
                    height="100%"
                    className="tenSVG"
                >
                    <defs>
                        <filter
                            id="filter973"
                            width="10.278"
                            height="10.306"
                            x="-0.139"
                            y="-0.153"
                            colorInterpolationFilters="sRGB"
                        >
                            <feGaussianBlur stdDeviation="10"></feGaussianBlur>
                        </filter>
                    </defs>
                    <animated.rect className="back"
                        style={{ mixBlendMode: "normal" }}
                        width="100%"
                        height="100%"
                        fill="#000"
                        fillOpacity="0.214"
                        filter="url(#filter973)"
                        opacity="0.6"
                        rx={round.round1}
                        ry={round.round2}
                    ></animated.rect>
                    <animated.rect className="font"
                        x={rest.xfront}
                        y="5%"
                        width={rest.widthfont}
                        height="90%"
                        fill="#fff"
                        rx={round.round1}
                        ry={round.round2}
                    ></animated.rect>

                </svg>

            </animated.div>
            <div className="eventDetector"
                onMouseEnter={() => {
                    setButton({ width: "20%" })
                    setRest({ widthfont: "100%", xfront: "0%" })
                    setRound({ round1: "10%", config: { duration: 90 } })
                }}
                onMouseLeave={() => {
                    setButton({ width: "5%" })
                    setRest({ widthfont: "90%", xfront: "5%" })
                    setRound({ round1: "50%", config: { duration: 800 } })
                }}
            ></div>
        </React.fragment>

    );
}
