import {
    call, take
} from "redux-saga/effects";
import axios from 'axios';
import store from '../store/store'
import { ActionTypes } from '../actions'

export function* watchUserSignIn(payload) {
    while (true) {
        const { payload } = yield take(ActionTypes.GET_ALL_EMAIL_LIST)
        yield call(getEmailList, payload)
    }
}

export function* getEmailList(payload) {
    const allEmailList = yield take(axios.get("https://emailengine2020.herokuapp.com/newemail/"))
    const filteredEmailList = allEmailList.filter(emailList => emailList.id === payload)
    yield console.log(filteredEmailList)
}
