import React, { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom'
import store from '../../../store/store'
import { storeCampaign } from '../../../actions'

const CampForm = ({ newCampaignData, setNewCampaignData }) => {

    // let update = false

    // const initialState = {
    //     senderName: "",//string
    //     senderEmail: "",//string
    //     campName: "",//string
    //     emailSub: "",//string
    //     emailListCVS: null,//file
    //     emailBodyJSON: null,//JSONobject
    //     emailBodyHTML: null,//HTMLobject 
    // }

    // const [currentState, setCurrentState] = useState(initialState)

    // useEffect(() => {
    //     if (store.getState().loadedCampaign.campId) {
    //         setCurrentState(store.getState().campaigns.filter(camps => camps.id === store.getState().loadedCampaign.campId))
    //         update = true
    //         console.log("got into if", currentState)
    //     } else setCurrentState(newCampaignData)
    // }, [newCampaignData])

    // console.log(currentState)


    const { push } = useHistory(null)
    return (
        <>
            <div className="topNavCam  subWrapperCam">
                <div className="createCamp   topButtonsCam subWrapperCam"
                    onClick={() => push("/dashboard")}
                >
                    Back
                </div>
                {/* <div className="addEmailList topButtonsCam subWrapperCam">
                    <p>Save & Next</p>
                </div> */}

            </div>
            <div className="dashBodyMainCam  subWrapperCam">
                <div className="camFormHolder">
                    <form className="formFields  Fields">
                        <div className="leftFields Fields">
                            <div className="subField Fields">
                                <label htmlFor="camName">Campaign Name</label>
                                <input value={currentState.campName} type="text" id="camName" name="camName"
                                    onChange={e => setNewCampaignData({ ...currentState, campName: e.target.value })}
                                />
                            </div>
                            <div className="subField Fields">
                                <label htmlFor="SenderName">Sender Name</label>
                                <input value={currentState.senderName} type="text" id="SenderName" name="SenderName"
                                    onChange={e => setNewCampaignData({ ...currentState, senderName: e.target.value })}
                                />
                            </div>
                            <div className="subField Fields">
                                <label htmlFor="emailSub">Email Subject</label>
                                <input value={currentState.emailSub} type="text" id="emailSub" name="camName"
                                    onChange={e => setNewCampaignData({ ...currentState, emailSub: e.target.value })}
                                />
                            </div>
                        </div>
                        <div className="rightFields Fields">
                            <div className="subField Fields">
                                <label htmlFor="SenderEmail">Sender Email</label>
                                <input value={currentState.senderEmail} type="text" id="SenderEmail" name="SenderEmail"
                                    onChange={e => setNewCampaignData({ ...currentState, senderEmail: e.target.value })}
                                />
                            </div>
                            <div className="subFieldLong Fields">
                                <label htmlFor="emailList">Email List</label>
                                {/* <select id="camName" name="camName">
                                <option value="NoEmailList">Please Create Email List</option>
                            </select> */}
                                <input type="file" id="emailList"
                                    onChange={e => setNewCampaignData({ ...currentState, emailListCVS: e.target.files[0] })}
                                />
                            </div>
                            <div className="subFieldLong Fields">
                                <label htmlFor="emailBody">Email Body</label>
                                {/* <select id="camName" name="camName">
                                <option value="NoEmailList">Please Create Email List</option>
                            </select> */}
                                <div id="emailBody" className="desBody Fields formButtonsCam"
                                    onClick={() => push("/dashboard/newcamp/maileditor")}
                                >
                                    Deisgn Body
                            </div>
                            </div>
                        </div>
                    </form>
                    <div className="formButtons Fields">
                        <div className="sendButton Fields formButtonsCam"

                        >
                            Save & Send{/* {update ? "Update & Send" : "Save & Send"} */}
                        </div>
                        <div className="createButton Fields formButtonsCam"
                            onClick={() => {
                                store.dispatch(storeCampaign(currentState));
                                push("/dashboard")
                            }
                            }
                        >
                            "Save & Exit" {/* {update ? "Update & Exit" : "Save & Exit"} */}
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default CampForm;
