import React from 'react';
import './sendpage.css';
import MailTemplate from './mailTemplates';
import {connect} from 'react-redux';


function SendPage(state) {
    return (
      <div className="sendpage">
          <div className="templateWrapper">
            {state.templates.map(templa=> <MailTemplate key={templa.key} name={templa.name}></MailTemplate>)}
          </div>  
      </div>
    );
  }

const mapStateToProps = (state) =>{
  return state
}

export default connect(mapStateToProps)(SendPage);
