import React, { useRef } from 'react';
import { render } from 'react-dom'
import './App.css';
import EmailEditor from 'react-email-editor';


function App() {
  const emailEditorRef = useRef(null);
  const styles = {width:"100vw", height:"100vw"}
  return (
    <React.Fragment>
      <div className="Nav">
            Navbar
      </div>
      <EmailEditor
        ref={emailEditorRef}
        style = {styles }
      />

    </React.Fragment>
  );
}

export default App;
