import React, { useEffect, useState } from 'react';
import Campaign from './campaign'
import store from '../../../store/store'

const Campaigns = () => {
    const [camps, setCamps] = useState(null)
    console.log(camps)
    // const updateCamps = () => {
    //     if (camps !== store.getState().capmpaigns) {
    //         setCamps(store.getState().capmpaigns)
    //     }
    // }
    useEffect(() => {
        store.subscribe(() => {
            if (camps !== store.getState().capmpaigns) {
                setCamps(store.getState().capmpaigns)
            }
        })
    }, [])


    return (
        <div className="subContentActual">
            {camps ? camps.map(camp => <Campaign key={camp.id} campId={camp.id} campName={camp.campName}></Campaign>) : null}
        </div>
    );
}

export default Campaigns;
